// WithNamedResources tells the query-builder to eager-load the nodes that are connected to the "resources"
// edge with the given name. The optional arguments are used to configure the query builder of the edge.
func (eq *ElementQuery) WithNamedResources(name string, opts ...func(*ResourceQuery)) *ElementQuery {
	query := (&ResourceClient{config: eq.config}).Query()
	for _, opt := range opts {
		opt(query)
	}
	if eq.withNamedResources == nil {
		eq.withNamedResources = make(map[string]*ResourceQuery)
	}
	eq.withNamedResources[name] = query
	return eq
}

